INSERT INTO `lore` VALUES ('55', '03001', '消息', '', '分为系统消息和一般消息，其中一般消息又分为待办消息和不接单消息\n\n系统消息：系统消息表：REGISTRAR_SYSMESSAGE、系统消息已推送记录：REGISTRAR_REGISTRAR_SYSMESSAGE、系统消息已读记录：REGISTRAR_READ_SYSMESSAGE\n\n一般消息：消息表：REGISTRAR_MESSAGE\n', '', '03');
INSERT INTO `lore` VALUES ('56', '030010001', '系统消息', '', '系统消息表：REGISTRAR_SYSMESSAGE\n系统消息已推送记录：REGISTRAR_REGISTRAR_SYSMESSAGE\n系统消息已读记录：REGISTRAR_READ_SYSMESSAGE\n\n给用户已经推送过写REGISTRAR_REGISTRAR_SYSMESSAGE表\n用户已经查看过写REGISTRAR_READ_SYSMESSAGE\n', '', '03001');
INSERT INTO `lore` VALUES ('57', '030010002', '一般消息', '', '消息表：REGISTRAR_MESSAGE\nmessage_type：消息类型，00：待办，01：不接单，02：系统 \nsign：已读、未读标志，0：已读，1：未读\npush_type：是否已推送，1：已推送，2：未推送\n', '', '03001');
